import { FC } from 'react';
import { Todo } from '../types/Todo';
import React from 'react';

type Props = {
  todo: Todo;
  deleteTodo: (todoId: number) => Promise<boolean>
};

export const TodoItem: FC<Props> = ({ todo, deleteTodo }) => {
  return (
    <div data-cy="Todo" className={`todo ${todo.completed ? 'completed' : ''}`}>
      {/* eslint-disable jsx-a11y/label-has-associated-control */}
      <label className="todo__status-label" htmlFor="TodoStatus">
        <input
          id="TodoStatus"
          data-cy="TodoStatus"
          type="checkbox"
          className="todo__status"
          checked={todo.completed}
        />
      </label>

      <span data-cy="TodoTitle" className="todo__title">
        {todo.title}
      </span>

      <button type="button" className="todo__remove" data-cy="TodoDelete">
        ×
      </button>
      {/*loader is required for tests, since updating todos not implemeted yet it has no effect on displayed todo*/}
      <div data-cy="TodoLoader" className="modal overlay">
        <div className="modal-background has-background-white-ter" />
        <div className="loader" />
      </div>
    </div>
  );
};
